import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Client } from '@pusher/push-notifications-web'; // Import Pusher Beams Client

const PatientRegistration = () => {
  const [formData, setFormData] = useState({
    name: '',
    dob: '',
    phoneNumber: '',
    email: '',
    gender: '',
    departmentId: '',
  });

  const [departments, setDepartments] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  // Initialize Pusher Beams Client
  const beamsClient = new Client({
    instanceId: '99d7ce8c-d3dc-4d8a-827f-f66779c8f1e8', // Replace with your Pusher Beams instance ID
  });

  const fetchDepartments = async () => {
    try {
      const response = await axios.get('http://localhost:3005/departments');
      setDepartments(response.data);
    } catch (error) {
      console.error('Error fetching departments:', error);
      setErrorMessage('Error fetching departments. Please try again later.');
    }
  };

  useEffect(() => {
    fetchDepartments();
  }, []);

  const handleChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('http://localhost:3005/register', formData);

      toast.success('Patient registered successfully!');

      // Send notification
      beamsClient.publishToInterests(['patients'], {
        web: {
          notification: {
            title: 'New Patient Registered',
            body: `Name: ${formData.name}, Department: ${formData.departmentId}`,
          },
        },
      });

      setFormData({
        name: '',
        dob: '',
        phoneNumber: '',
        email: '',
        gender: '',
        departmentId: '',
      });

      console.log('Patient registered successfully:', response.data);
    } catch (error) {
      console.error('Error registering patient:', error);
      setErrorMessage('Failed to register patient. Please try again.');
    }
  };

  return (
    <div>
      <h1>Patient Registration</h1>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      <form onSubmit={handleSubmit}>
        {/* Form inputs */}
      </form>
      <ToastContainer />
    </div>
  );
};

export default PatientRegistration;
