import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DoctorInterface = () => {
  const [selectedDepartment, setSelectedDepartment] = useState('');
  const [patients, setPatients] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    fetchDepartments();
  }, []);

  const fetchDepartments = async () => {
    try {
      const response = await axios.get('http://localhost:3005/departments');
      setDepartments(response.data);
    } catch (error) {
      console.error('Error fetching departments:', error);
      setErrorMessage('Error fetching departments. Please try again later.');
    }
  };

  const fetchPatients = async () => {
    try {
      const response = await axios.get(`http://localhost:3005/patients/${selectedDepartment}`);
      setPatients(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
      setErrorMessage('Error fetching patients. Please try again later.');
    }
  };

  const handleDepartmentChange = (event) => {
    setSelectedDepartment(event.target.value);
    fetchPatients();
  };

  const handlePatientStatusChange = async (patientId) => {
    try {
      await axios.put(`http://localhost:3005/patients/${selectedDepartment}/${patientId}/status`, { status: 'done' });
      // Optionally, update patient status in state or refetch patients
    } catch (error) {
      console.error('Error updating patient status:', error);
      setErrorMessage('Error updating patient status. Please try again later.');
    }
  };

  return (
    <div>
      <h1>Doctor Interface</h1>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      <div>
        <label htmlFor="department">Select Department:</label>
        <select id="department" value={selectedDepartment} onChange={handleDepartmentChange}>
          <option value="">Select Department</option>
          {departments.map((department) => (
            <option key={department.name} value={department.name}>
              {department.name}
            </option>
          ))}
        </select>
      </div>
      <div>
        <h2>Patients</h2>
        {patients.map((patient) => (
          <div key={patient.id}>
            <p>{patient.name}</p>
            <p>{patient.status}</p>
            <button onClick={() => handlePatientStatusChange(patient.id)}>Mark as Done</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DoctorInterface;
