import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const PatientRegistration = () => {
  const [formData, setFormData] = useState({
    name: '',
    dob: '',
    phoneNumber: '',
    email: '',
    gender: '',
    departmentId: '',
  });

  const [departments, setDepartments] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    fetchDepartments();
  }, []);

  const fetchDepartments = async () => {
    try {
      const response = await axios.get('http://localhost:3005/departments');
      setDepartments(response.data);
    } catch (error) {
      console.error('Error fetching departments:', error);
      setErrorMessage('Error fetching departments. Please try again later.');
    }
  };

  const handleChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Form submission code...
    } catch (error) {
      console.error('Error registering patient:', error);
      setErrorMessage('Failed to register patient. Please try again.');
    }
  };

  return (
    <div>
      <h1>Patient Registration</h1>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      <form onSubmit={handleSubmit}>
        {/* Input fields... */}
        <label htmlFor="departmentId">Department:</label>
        <select id="departmentId" name="departmentId" value={formData.departmentId || ''} onChange={handleChange} required>
          <option value="">Select Department</option>
          {departments.map((department) => (
            <option key={department.departmentId} value={department.departmentId}>
              {department.name}
            </option>
          ))}
        </select>
        <br />
        <button type="submit">Register Patient</button>
      </form>
      <ToastContainer />
    </div>
  );
};

export default PatientRegistration;
